import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';


function TimeSeetTable() {
    const [data, setData] = useState([]);
    const [employee, setEmployee] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [limit] = useState(10);
    const [form, setForm] = useState({
      id: null,
      employee: '',
      reason: '',
      startDate: '',
      duration: '',
      discounted: false,
      description: ''
    });
    const [editId, setEditId] = useState(null);
    const [originalData, setOriginalData] = useState(null);
  
    const apiUrl = process.env.REACT_APP_API_URL;
  
    useEffect(() => {
      fetchEmployees();
      fetchData(currentPage, limit);
    }, [currentPage, limit]);
  
    const fetchEmployees = async () => {
      try {
        const res = await axios.get(`${apiUrl}/employee`);
        setEmployee(res.data);
      } catch (error) {
        console.error('Error App fetchEmployees: ', error);
      }
    };
  
    const fetchData = async (page, limit) => {
      try {
        const res = await axios.get(`${apiUrl}/TimeSheet`, {
          params: { page, limit }
        });
        setData(res.data.timeSheets);
        setCurrentPage(res.data.currentPage);
        setTotalPages(res.data.totalPages);
      } catch (error) {
        console.error('Error App fetchData: ', error);
      }
    };
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setForm({
        ...form,
        [name]: value
      });
    };
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      try {
        if (form.id) {
          await axios.put(`${apiUrl}/${form.id}`, form);
        } else {
          await axios.post(`${apiUrl}`, form);
        }
        fetchData(currentPage, limit);
        setForm({
          id: null,
          employees: '',
          reason: '',
          startDate: '',
          duration: '',
          discounted: false,
          description: ''
        });
        setEditId(null);
        setOriginalData(null);
      } catch (error) {
        console.error('Error App handleSubmit: ', error);
      }
    };
  
    const handleEdit = (record) => {
      setEditId(record.id);
      setForm(record);
      setOriginalData(record);
    };
  
    const handleDelete = (record) => {
      setEditId(record.id);
      setForm(record);
      setOriginalData(record);
    };
    
    const handleCancel = () => {
      setEditId(null);
      setForm({
        id: null,
        employees: '',
        reason: '',
        startDate: '',
        duration: '',
        discounted: false,
        description: ''
      });
      setOriginalData(null);
    };
  
    const handleSave = async () => {
      try {
        await axios.put(`${apiUrl}/${form.id}`, form);
        fetchData(currentPage, limit);
        setEditId(null);
        setOriginalData(null);
      } catch (error) {
        console.error('Error App handleSave: ', error);
      }
    };
  
    const handlePreviousPage = () => {
      if (currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    };
  
    const handleNextPage = () => {
      if (currentPage < totalPages) {
        setCurrentPage(currentPage + 1);
      }
    };
  
    
    return (
        <div>
        <table>
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Причина отсутствия</th>
              <th>Дата начала</th>
              <th>Продолжительность (раб. дней)</th>
              <th>Учтено при оплате</th>
              <th>Комментарий</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            
            {data && data.map((item) => {
              const  employeeInfo = employee.find(emp => emp.id === item.employee);
            return (
              
              <tr key={item.id}>
                <td>{`${employeeInfo.firstName} ${employeeInfo.lastName} (${employeeInfo.id})`}</td>
                <td>{item.reason === 1 ? 'Отпуск' : item.reason === 2 ? 'Больничный' : 'Прогул'}</td>
                <td>{Date.parse(item.startDate)}</td>
                <td>{item.duration}</td>
                <td>{item.discounted ? 'Да' : 'Нет'}</td>
                <td>{item.description}</td>
                <td>
                  {editId === item.id ? (
                    <>
                      <button onClick={handleSave}>Сохранить</button>
                      <button onClick={handleCancel}>Отмена</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => handleEdit(item)}>Изменить</button>
                      <button onClick={() => handleDelete(item)}>Удалить</button>
                    </>               
                  )}
                </td>
              </tr>
            )})}
          </tbody>
        </table>
        <div className="pagination">
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>Назад</button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={handleNextPage} disabled={currentPage === totalPages}>Далее</button>
        </div>
        </div>
    );
  }
  
  export default TimeSeetTable;
