import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import TimeSeetRow from './TimeSeetRow';



function TimeSeetTable({timeSeet, employees, updateTimeSeet, deleteTimeSeet}) {
    
  
    
    return (
        <table>
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Причина отсутствия</th>
              <th>Дата начала</th>
              <th>Продолжительность (раб. дней)</th>
              <th>Учтено при оплате</th>
              <th>Комментарий</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {data.map(data => (
                <TimeSeetRow
                    key = {data.ID}
                    timeSeet = {data}
                    employee = {employees.find((emp) => data.employee === emp.id)}
                    updateTimeSeet = {updateTimeSeet}
                    deleteTimeSeet = {deleteTimeSeet}
                />
            ))}
            
          </tbody>
        </table>
    );
  }
  
  export default TimeSeetTable;
