import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';


function TimeSeetRow ({ timeSeet, employees, updateTimeSeet, deleteTimeSeet }) {
    const [isEditing, setIsEditing] = useState(false);
    const [editedTimeSeet, setEditedTimeSeet] = useState({ ...timeSeet });
  
    const handleEditChange = (e) => {
        const { name, value } = e.target;
        setEditedTimeSeet({ ...editedTimeSeet, [name]: value });
    };
    
    const handleSave = () => {
        fetch(`/api/TimeSeets/${editedTimeSeet.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(editedTimeSeet),
        })
            .then(response => response.json())
            .then(data => {
                updateTimeSeet(data);
                setIsEditing(false);
            })
            .catch(error => console.error('Error updating TimeSeet:', error));
    };

    const handleDelete = () => {
        fetch(`/api/TimeSeets/${timeSeet.id}`, {
            method: 'DELETE',
        })
            .then(() => deleteTimeSeet(timeSeet.id))
            .catch(error => console.error('Error deleting TimeSeet:', error));
    };
   
    return (
        <tr key={timeSeet.key}>
            <td>{isEditing ? <select name="employee" value={editedTimeSeet.employee} onChange={handleEditChange}>
                <option key={employees.id} value={employees.id}>{`${employees.firstName} ${employees.lastName} (${employees.id})`}</option>
                </select> : timeSeet.employee}</td>
            <td>{isEditing ? <select name="reason" value={editedTimeSeet.reason} onChange={handleEditChange}>
                <option value="">Выбор причины</option>
              <option value="1">Отпуск</option>
              <option value="2">Больничный</option>
              <option value="3">Прогул</option>
            </select> : timeSeet.reason}</td>
            <td>{isEditing ? <input type="date" name="startDate" value={editedTimeSeet.startDate} onChange={handleEditChange} /> : timeSeet.startDate}</td>
            <td>{isEditing ? <input type="number" min="1" name="duration" value={editedTimeSeet.duration} onChange={handleEditChange} /> : timeSeet.duration}</td>
            <td>{isEditing ? <input type="checkbox" name="discounted" value={editedTimeSeet.discounted} onChange={handleEditChange} /> : timeSeet.discounted}</td>
            <td>{isEditing ? <textarea name="description" value={editedTimeSeet.description} onChange={handleEditChange} /> : timeSeet.description}</td>
            <td>
                {isEditing ? (
                    <>
                      <button onClick={handleSave}>Сохранить</button>
                      <button onClick={() => setIsEditing(false)}>Отмена</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => setIsEditing(true)}>Изменить</button>
                      <button onClick={handleDelete}>Удалить</button>
                    </>               
                  )}
                </td>
        </tr>
    );
  }
  
  export default TimeSeetRow;
