import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import TimeSeetRow from './TimeSeetRow';



function TimeSeetTable({timeSeet, employees, updateTimeSeet, deleteTimeSeet}) {
    const [data, setData] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [limit] = useState(10);
  
    useEffect(() => {
        fetchData(currentPage, limit);
    }, [currentPage,  limit]);
    
    

    const fetchData = async (page, limit) => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/TimeSheet`, {
          params: { page, limit }
        });
        setData(res.data.timeSheets);
        setCurrentPage(res.data.currentPage);
        setTotalPages(res.data.totalPages);
      } catch (error) {
        console.error('Error App fetchData: ', error);
      }
    };

    const handlePreviousPage = () => {
      if (currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    };
  
    const handleNextPage = () => {
      if (currentPage < totalPages) {
        setCurrentPage(currentPage + 1);
      }
    };
  
    
    return (
        <div>
        <table>
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Причина отсутствия</th>
              <th>Дата начала</th>
              <th>Продолжительность (раб. дней)</th>
              <th>Учтено при оплате</th>
              <th>Комментарий</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {data.map(data => (
                <TimeSeetRow
                    key = {data.ID}
                    timeSeet = {data}
                    employee = {employees.find((emp) => data.employee === emp.id)}
                    updateTimeSeet = {updateTimeSeet}
                    deleteTimeSeet = {deleteTimeSeet}
                />
            ))}
            
          </tbody>
        </table>
        <div className="pagination">
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>Назад</button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={handleNextPage} disabled={currentPage === totalPages}>Далее</button>
        </div>
        </div>
    );
  }
  
  export default TimeSeetTable;
