import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [limit] = useState(10);
  const [form, setForm] = useState({
    id: null,
    employee: '',
    reason: '',
    start_date: '',
    duration: '',
    discounted: false,
    description: ''
  });
  const [editId, setEditId] = useState(null);
  const [originalData, setOriginalData] = useState(null);

  const apiUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    fetchEmployees();
    fetchData(currentPage, limit);
  }, [currentPage, limit]);

  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${apiUrl}/employees`);
      setEmployees(res.data);
    } catch (error) {
      console.error('Error App fetchEmployees: ', error);
    }
  };

  const fetchData = async (page, limit) => {
    try {
      const res = await axios.get(`${apiUrl}/TimeSheet`, {
        params: { page, limit }
      });
      setData(res.data.items);
      setCurrentPage(res.data.page);
      setTotalPages(res.data.totalPages);
    } catch (error) {
      console.error('Error App fetchData: ', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({
      ...form,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (form.id) {
        await axios.put(`${apiUrl}/${form.id}`, form);
      } else {
        await axios.post(`${apiUrl}`, form);
      }
      fetchData(currentPage, limit);
      setForm({
        id: null,
        employee: '',
        reason: '',
        start_date: '',
        duration: '',
        discounted: false,
        description: ''
      });
      setEditId(null);
      setOriginalData(null);
    } catch (error) {
      console.error('Error App handleSubmit: ', error);
    }
  };

  const handleEdit = (record) => {
    setEditId(record.id);
    setForm(record);
    setOriginalData(record);
  };

  const handleDelete = (record) => {
    setEditId(record.id);
    setForm(record);
    setOriginalData(record);
  };

  const handleCancel = () => {
    setEditId(null);
    setForm({
      id: null,
      employee: '',
      reason: '',
      start_date: '',
      duration: '',
      discounted: false,
      description: ''
    });
    setOriginalData(null);
  };

  const handleSave = async () => {
    try {
      await axios.put(`${apiUrl}/${form.id}`, form);
      fetchData(currentPage, limit);
      setEditId(null);
      setOriginalData(null);
    } catch (error) {
      console.error('Error App handleSave: ', error);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  return (
    <div className="container">
      <div className="App">
        <h1>Учет отсутствия сотрудников на рабочем месте</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Сотрудник:</label>
            <select name="employee" value={form.employee} onChange={handleChange} required>
              <option value="">Выбор сотрудника</option>
              {employees.map((emp) => (
                <option key={emp.id} value={emp.id}>{emp.name}</option>
              ))}
            </select>
          </div>
          <div>
            <label>Причина отсутствия:</label>
            <select name="reason" value={form.reason} onChange={handleChange} required>
            <option value="">Выбор причины</option>
              <option value="1">Отпуск</option>
              <option value="2">Больничный</option>
              <option value="3">Прогул</option>
            </select>
          </div>
          <div>
            <label>Дата начала:</label>
            <input type="date" name="start_date" value={form.start_date} onChange={handleChange} required />
          </div>
          <div>
            <label>Продолжительность (раб. дней):</label>
            <input type="number" min="1" name="duration" value={form.duration} onChange={handleChange} required />
          </div>
          <div>
            <label>Учтено при оплате:</label>
            <input type="checkbox" name="discounted" checked={form.discounted} onChange={(e) => setForm({ ...form, discounted: e.target.checked })} />
          </div>
          <div>
            <label>Комментарий:</label>
            <textarea name="description" value={form.description} onChange={handleChange} />
          </div>
          <button type="submit">{form.id ? 'Обновить' : 'Добавить'}</button>
        </form>
        <table>
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Причина отсутствия</th>
              <th>Дата начала</th>
              <th>Продолжительность (раб. дней)</th>
              <th>Учтено при оплате</th>
              <th>Комментарий</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                <td>{employees.find(emp => emp.id === item.employee)?.name}</td>
                <td>{item.reason === 1 ? 'Отпуск' : item.reason === 2 ? 'Больничный' : 'Прогул'}</td>
                <td>{item.startDate}</td>
                <td>{item.duration}</td>
                <td>{item.discounted ? 'Да' : 'Нет'}</td>
                <td>{item.description}</td>
                <td>
                  {editId === item.id ? (
                    <>
                      <button onClick={handleSave}>Сохранить</button>
                      <button onClick={handleCancel}>Отмена</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => handleEdit(item)}>Изменить</button>
                      <button onClick={() => handleDelete(item)}>Удалить</button>
                    </>               
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className="pagination">
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>Назад</button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={handleNextPage} disabled={currentPage === totalPages}>Далее</button>
        </div>
      </div>
    </div>
  );
}

export default App;
