import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import TimeSeetRow from './TimeSeetRow';



function TimeSeetTable() {
    const [data, setData] = useState([]);
    const [employee, setEmployee] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [limit] = useState(10);
    const [form, setForm] = useState({
      id: null,
      employee: '',
      reason: '',
      startDate: '',
      duration: '',
      discounted: false,
      description: ''
    });
    const [editId, setEditId] = useState(null);
    const [originalData, setOriginalData] = useState(null);
  
    const apiUrl = process.env.REACT_APP_API_URL;
  
    useEffect(() => {
      fetchEmployees();
      fetchData(currentPage, limit);
    }, [currentPage, limit]);
  
    const fetchEmployees = async () => {
      try {
        const res = await axios.get(`${apiUrl}/employee`);
        setEmployee(res.data);
      } catch (error) {
        console.error('Error App fetchEmployees: ', error);
      }
    };
  
    const fetchData = async (page, limit) => {
      try {
        const res = await axios.get(`${apiUrl}/TimeSheet`, {
          params: { page, limit }
        });
        setData(res.data.timeSheets);
        setCurrentPage(res.data.currentPage);
        setTotalPages(res.data.totalPages);
      } catch (error) {
        console.error('Error App fetchData: ', error);
      }
    };

    const handlePreviousPage = () => {
      if (currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    };
  
    const handleNextPage = () => {
      if (currentPage < totalPages) {
        setCurrentPage(currentPage + 1);
      }
    };
  
    
    return (
        <div>
        <table>
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Причина отсутствия</th>
              <th>Дата начала</th>
              <th>Продолжительность (раб. дней)</th>
              <th>Учтено при оплате</th>
              <th>Комментарий</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            
            <TimeSeetRow/>

          </tbody>
        </table>
        <div className="pagination">
          <button onClick={handlePreviousPage} disabled={currentPage === 1}>Назад</button>
          <span>Страница {currentPage} из {totalPages}</span>
          <button onClick={handleNextPage} disabled={currentPage === totalPages}>Далее</button>
        </div>
        </div>
    );
  }
  
  export default TimeSeetTable;
