import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';


function TimeSeetRow() {
    const [data, setData] = useState([]);
    const [employee, setEmployee] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [limit] = useState(10);
    const [form, setForm] = useState({
      id: null,
      employee: '',
      reason: '',
      startDate: '',
      duration: '',
      discounted: false,
      description: ''
    });
    const [editId, setEditId] = useState(null);
    const [originalData, setOriginalData] = useState(null);
  
    const apiUrl = process.env.REACT_APP_API_URL;
  
  
    const handleEdit = (record) => {
      setEditId(record.id);
      setForm(record);
      setOriginalData(record);
    };
  
    const handleDelete = (record) => {
      setEditId(record.id);
      setForm(record);
      setOriginalData(record);
    };
    
    const handleCancel = () => {
      setEditId(null);
      setForm({
        id: null,
        employees: '',
        reason: '',
        startDate: '',
        duration: '',
        discounted: false,
        description: ''
      });
      setOriginalData(null);
    };
  
    
    
    return (
        <div>
             {data && data.map((item) => {
              const  employeeInfo = employee.find(emp => emp.id === item.employee);
            return (
              
              <tr key={item.id}>
                <td>{`${employeeInfo.firstName} ${employeeInfo.lastName} (${employeeInfo.id})`}</td>
                <td>{item.reason === 1 ? 'Отпуск' : item.reason === 2 ? 'Больничный' : 'Прогул'}</td>
                <td>{Date.parse(item.startDate)}</td>
                <td>{item.duration}</td>
                <td>{item.discounted ? 'Да' : 'Нет'}</td>
                <td>{item.description}</td>
                <td>
                  {editId === item.id ? (
                    <>
                      <button onClick={handleSave}>Сохранить</button>
                      <button onClick={handleCancel}>Отмена</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => handleEdit(item)}>Изменить</button>
                      <button onClick={() => handleDelete(item)}>Удалить</button>
                    </>               
                  )}
                </td>
              </tr>
            )})}
        </div>
    );
  }
  
  export default TimeSeetRow;
